import React, { useState, useEffect } from 'react';

interface AccessibilitySettings {
  fontSize: 'normal' | 'large' | 'extra-large';
  contrast: 'normal' | 'high';
  spacing: 'normal' | 'wide';
  cursor: 'normal' | 'large';
}

const AccessibilityWidget: React.FC = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [settings, setSettings] = useState<AccessibilitySettings>({
    fontSize: 'normal',
    contrast: 'normal',
    spacing: 'normal',
    cursor: 'large'
  });

  // Apply accessibility settings to the document
  useEffect(() => {
    const root = document.documentElement;

    // Font size
    root.style.setProperty('--accessibility-font-size', 
      settings.fontSize === 'large' ? '1.2' : 
      settings.fontSize === 'extra-large' ? '1.4' : '1'
    );

    // Contrast
    if (settings.contrast === 'high') {
      root.style.setProperty('--accessibility-contrast', 'high');
    } else {
      root.style.removeProperty('--accessibility-contrast');
    }

    // Spacing
    root.style.setProperty('--accessibility-spacing', 
      settings.spacing === 'wide' ? '1.5' : '1'
    );

    // Cursor
    if (settings.cursor === 'large') {
      root.style.setProperty('--accessibility-cursor', 'large');
    } else {
      root.style.removeProperty('--accessibility-cursor');
    }
  }, [settings]);

  const updateSetting = (key: keyof AccessibilitySettings, value: any) => {
    setSettings(prev => ({ ...prev, [key]: value }));
  };

  return (
    <>
      {/* Floating Accessibility Button */}
      <button
        onClick={() => setIsOpen(!isOpen)}
        className="fixed bottom-24 right-6 z-50 bg-blue-600 hover:bg-blue-700 text-white w-14 h-14 rounded-full shadow-lg transition-all duration-300 transform hover:scale-110 flex items-center justify-center"
        aria-label="Accessibility Options"
      >
        <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 10V3L4 14h7v7l9-11h-7z" />
        </svg>
      </button>

      {/* Accessibility Panel */}
      {isOpen && (
        <div className="fixed bottom-32 right-6 z-50 bg-white rounded-2xl shadow-2xl border border-gray-200 w-80 max-h-96 overflow-y-auto">
          <div className="p-6">
            <div className="flex items-center justify-between mb-6">
              <h3 className="text-lg font-bold text-gray-900">Accessibility</h3>
              <button
                onClick={() => setIsOpen(false)}
                className="text-gray-400 hover:text-gray-600 transition-colors"
              >
                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                </svg>
              </button>
            </div>

            {/* Font Size */}
            <div className="mb-6">
              <label className="block text-sm font-medium text-gray-700 mb-3">
                Text Size
              </label>
              <div className="space-y-2">
                {[
                  { value: 'normal', label: 'Normal' },
                  { value: 'large', label: 'Large' },
                  { value: 'extra-large', label: 'Extra Large' }
                ].map((option) => (
                  <label key={option.value} className="flex items-center">
                    <input
                      type="radio"
                      name="fontSize"
                      value={option.value}
                      checked={settings.fontSize === option.value}
                      onChange={(e) => updateSetting('fontSize', e.target.value)}
                      className="mr-3 text-blue-600 focus:ring-blue-500"
                    />
                    <span className="text-sm text-gray-700">{option.label}</span>
                  </label>
                ))}
              </div>
            </div>

            {/* Contrast */}
            <div className="mb-6">
              <label className="block text-sm font-medium text-gray-700 mb-3">
                Contrast
              </label>
              <div className="space-y-2">
                {[
                  { value: 'normal', label: 'Normal' },
                  { value: 'high', label: 'High Contrast' }
                ].map((option) => (
                  <label key={option.value} className="flex items-center">
                    <input
                      type="radio"
                      name="contrast"
                      value={option.value}
                      checked={settings.contrast === option.value}
                      onChange={(e) => updateSetting('contrast', e.target.value)}
                      className="mr-3 text-blue-600 focus:ring-blue-500"
                    />
                    <span className="text-sm text-gray-700">{option.label}</span>
                  </label>
                ))}
              </div>
            </div>

            {/* Spacing */}
            <div className="mb-6">
              <label className="block text-sm font-medium text-gray-700 mb-3">
                Line Spacing
              </label>
              <div className="space-y-2">
                {[
                  { value: 'normal', label: 'Normal' },
                  { value: 'wide', label: 'Wide' }
                ].map((option) => (
                  <label key={option.value} className="flex items-center">
                    <input
                      type="radio"
                      name="spacing"
                      value={option.value}
                      checked={settings.spacing === option.value}
                      onChange={(e) => updateSetting('spacing', e.target.value)}
                      className="mr-3 text-blue-600 focus:ring-blue-500"
                    />
                    <span className="text-sm text-gray-700">{option.label}</span>
                  </label>
                ))}
              </div>
            </div>

            {/* Cursor */}
            <div className="mb-6">
              <label className="block text-sm font-medium text-gray-700 mb-3">
                Cursor Size
              </label>
              <div className="space-y-2">
                {[
                  { value: 'normal', label: 'Normal' },
                  { value: 'large', label: 'Large' }
                ].map((option) => (
                  <label key={option.value} className="flex items-center">
                    <input
                      type="radio"
                      name="cursor"
                      value={option.value}
                      checked={settings.cursor === option.value}
                      onChange={(e) => updateSetting('cursor', e.target.value)}
                      className="mr-3 text-blue-600 focus:ring-blue-500"
                    />
                    <span className="text-sm text-gray-700">{option.label}</span>
                  </label>
                ))}
              </div>
            </div>

            {/* Reset Button */}
            <button
              onClick={() => setSettings({
                fontSize: 'normal',
                contrast: 'normal',
                spacing: 'normal',
                cursor: 'large'
              })}
              className="w-full bg-gray-100 hover:bg-gray-200 text-gray-700 font-medium py-2 px-4 rounded-lg transition-colors duration-200"
            >
              Reset to Default
            </button>
          </div>
        </div>
      )}
    </>
  );
};

export default AccessibilityWidget;
